$(document).ready(function () {
    $(".ir-province").each(loadProvinces), $(".ir-province").change(loadCities);
});

var loadProvinces = function () {
    var t = $(this);
    t.empty(),
        $.each(data, function (a, i) {
            var o = $("<option></option>").attr("value", a).text(a);
            t.append(o);
        });
},
loadCities = function () {
    var t = $(this).closest("div.ir-select").find(".ir-city"),
        a = $(this).val(),
        i = data[a];
    t.empty(),
        $.each(i, function (a, i) {
            var o = $("<option></option>").attr("value", i).text(i);
            t.append(o);
        });
};
